                <input type="number" placeholder="Comisi칩n (%)" value={modalPlayer.club.commissionPercentage} onChange={(e) => setModalPlayer(prev => ({ ...prev, club: { ...prev.club, commissionPercentage: e.target.value } }))} className="w-full p-3 mb-4 bg-gray-700 text-white rounded-xl border border-gray-600 focus:outline-none focus:ring-2 focus:ring-blue-500" />
                <input type="number" placeholder="Monto Fijo de Comisi칩n" value={modalPlayer.club.commissionFixed} onChange={(e) => setModalPlayer(prev => ({ ...prev, club: { ...prev.club, commissionFixed: e.target.value } }))} className="w-full p-3 mb-4 bg-gray-700 text-white rounded-xl border border-gray-600 focus:outline-none focus:ring-2 focus:ring-blue-500" />
                <input type="url" placeholder="URL Perfil Transfermarkt" value={modalPlayer.club.transfermarktProfile} onChange={(e) => setModalPlayer(prev => ({ ...prev, club: { ...prev.club, transfermarktProfile: e.target.value } }))} className="w-full p-3 mb-4 bg-gray-700 text-white rounded-xl border border-gray-600 focus:outline-none focus:ring-2 focus:ring-blue-500" />
                <input type="text" placeholder="Valoraci칩n Transfermarkt" value={modalPlayer.club.transfermarktValuation} onChange={(e) => setModalPlayer(prev => ({ ...prev, club: { ...prev.club, transfermarktValuation: e.target.value } }))} className="w-full p-3 bg-gray-700 text-white rounded-xl border border-gray-600 focus:outline-none focus:ring-2 focus:ring-blue-500" />
              </div>
              {/* Secci칩n Otros */}
              <div>
                <h5 className="text-lg font-medium text-gray-300 mb-3">Otros</h5>
                <input type="text" placeholder="Direcci칩n" value={modalPlayer.other.address} onChange={(e) => setModalPlayer(prev => ({ ...prev, other: { ...prev.other, address: e.target.value } }))} className="w-full p-3 mb-4 bg-gray-700 text-white rounded-xl border border-gray-600 focus:outline-none focus:ring-2 focus:ring-blue-500" />
                <input type="tel" placeholder="Tel칠fono" value={modalPlayer.other.phone} onChange={(e) => setModalPlayer(prev => ({ ...prev, other: { ...prev.other, phone: e.target.value } }))} className="w-full p-3 mb-4 bg-gray-700 text-white rounded-xl border border-gray-600 focus:outline-none focus:ring-2 focus:ring-blue-500" />
                <input type="email" placeholder="Correo Electr칩nico de Contacto" value={modalPlayer.other.email} onChange={(e) => setModalPlayer(prev => ({ ...prev, other: { ...prev.other, email: e.target.value } }))} className="w-full p-3 mb-4 bg-gray-700 text-white rounded-xl border border-gray-600 focus:outline-none focus:ring-2 focus:ring-blue-500" />
                <input type="url" placeholder="URL de Instagram" value={modalPlayer.other.instagram} onChange={(e) => setModalPlayer(prev => ({ ...prev, other: { ...prev.other, instagram: e.target.value } }))} className="w-full p-3 mb-4 bg-gray-700 text-white rounded-xl border border-gray-600 focus:outline-none focus:ring-2 focus:ring-blue-500" />
                <div className="mb-4">
                  <label className="block text-sm font-medium text-gray-400 mb-2">Fotos (m치x. 3):</label>
                  {modalPlayer.other.photoUrls && modalPlayer.other.photoUrls.length > 0 && (
                    <div className="flex flex-wrap gap-2 mb-2">
                      {modalPlayer.other.photoUrls.map((url, idx) => (
                        <div key={idx} className="relative w-20 h-20 rounded-xl overflow-hidden border border-gray-600">
